// -*- js-indent-level: 2 -*-

var events = require('events');
var fs = require('fs');
var path = require('path');
var jade = require('jade');
var util = require('util');
var mime = require('mime');
var filesize = require('filesize');

// Template engine
var gen_files_view = jade.compile([
    '- each file in files',
    '  .file(data-path="#{file.path}")',
    '    .icon',
    '      img(src="assets/icons/#{file.type}.png")',
    '    .name #{file.name}',
    '    .size #{file.size}',
    '    .type #{file.type}',
    '    .mtime #{file.mtime}',
].join('\n'));

// Our type
function Folder(jquery_element) {
  events.EventEmitter.call(this);
  this.element = jquery_element; // '#files'

  var self = this;
  // Click on blank
  this.element.parent().on('click', function() {
    self.element.children('.focus').removeClass('focus');
  });
  // Click on file
  this.element.delegate('.file', 'click', function(e) {
    self.element.children('.focus').removeClass('focus');
    $(this).addClass('focus');
    e.stopPropagation();
  });
  // Double click on file
  this.element.delegate('.file', 'dblclick', function() {
    var file_path = $(this).attr('data-path');
    self.emit('navigate', file_path, mime.stat(file_path));
  });
  // Right click: context Menu
  this.element.delegate('.file', 'contextmenu', function(ev){
    // there are other context menu for blank space
    ev.stopPropagation();

    self.element.children('.focus').removeClass('focus');
    $(this).addClass('focus');

    var file_element = $(this);
    self.emit('contextmenu', file_element, ev);
  });
}

util.inherits(Folder, events.EventEmitter);

Folder.prototype.open = function(dir) {
  var self = this;
  var showHiddenFiles = user_configs.Views.ShowHiddenFiles;

  if (dir === "<about:trash-empty>") {
      self.element.html("");
      return;
  }

  fs.readdir(dir, function(error, files) {
    if (error) {
      console.log(error);
      window.alert(error);
      return;
    }

    if (!showHiddenFiles) {
       files = files.filter(function(file){
	  return !(/(^|.\/)\.+[^\/\.]/g).test(file);
       });
    }
    
    files = files.map(function(file){
       var fpath = path.join(dir, file);
       var mstat = mime.stat(fpath);
       var fstat = fs.statSync(fpath);
       if (fstat.isDirectory()) {
            var child = fs.readdirSync(fpath);
            if (child.length === 0 || child.length === 1) {
               fstat.size = child.length + " item";
            } else {
               fstat.size = child.length + " items";
            }
       } else {
            fstat.size = filesize(fstat.size);
       }
       return {"path":mstat.path, "name":mstat.name, "type":mstat.type, "size":fstat.size, "mtime":fstat.mtime};
    });

    self.element.html(gen_files_view({ files: files }));
  });
}

exports.Folder = Folder; 
